export const allQuestions: Question[] = [
  ...gkQuestions,
  ...gsQuestions,
  ...physicsQuestions,
  ...mathQuestions,
  ...sscQuestions,
  ...onedayQuestions,
];

export function getQuestionsForSubject(subject: SubjectType): Question[] {
  return allQuestions.filter(q => q.subject === subject);
}

export function getQuestionsForLevel(subject: SubjectType, level: number): Question[] {
  const subjectQuestions = getQuestionsForSubject(subject);
  const startIndex = (level - 1) * 10;
  const endIndex = startIndex + 10;
  return subjectQuestions.slice(startIndex, endIndex);
}

